mapper.select:
SELECT id, writer, title, content, date_format(reg_date, '%Y-%m-%d %H:%i:%s') as regDate, view_cnt as viewCnt
FROM board
WHERE id = #{id}

mapper.update:
UPDATE viewCnt
SET view_cnt = view_cnt+1
WHERE id = #{id}

UPDATE board
SETE title = #{title}, content = #{content}, reg_date = now()
WHERE id = #{id}
